        public function byCode(string $code): Country
        {
            $cnt = json_decode(COUNTRIES_STRING);

            for($i = 0; $i < count($cnt); $i++)
            {
                if($cnt[$i]->code == trim(strtolower($code)))
                {
                    $ret = new Country();
                    $ret->name = $cnt[$i]->name;
                    $ret->code = $cnt[$i]->code;
                    $ret->capital = $cnt[$i]->capital;
                    $ret->phoneCode = $cnt[$i]->phoneCode;
                    $ret->continent = $cnt[$i]->continent;
                    $ret->currency = $cnt[$i]->currency;
                    $ret->timeZone = $cnt[$i]->timeZone;
                    $ret->region = $cnt[$i]->region;
                    $ret->subRegion = $cnt[$i]->subRegion;
                    $ret->code3 = $cnt[$i]->code3;
                    $ret->language = $cnt[$i]->language;

                    return $ret;
                }
            }
            return new Country();
        }


        public function fromCode(string $code): void
        {
            $cnt = json_decode(COUNTRIES_STRING);

            for($i = 0; $i < count($cnt); $i++)
