        public static function ByCode(string $code): Currency
        {
            $cnt = json_decode(CURRENCY_STRING);

            for($i = 0; $i < count($cnt); $i++)
            {
                if($cnt[$i]->code == trim(strtolower($code)))
                {
                    $ret = new Currency();
                    $ret->name = $cnt[$i]->name;
                    $ret->code = $cnt[$i]->code;
                    $ret->country = $cnt[$i]->country;
                    $ret->symbol = $cnt[$i]->symbol;
                    $ret->countryCode = $cnt[$i]->countryCode;

                    return $ret;
                }
            }
            return new Currency();
        }

        private function fromCode(string $code): void
        {
            $cnt = json_decode(CURRENCY_STRING);

            for($i = 0; $i < count($cnt); $i++)
            {
                if($cnt[$i]->code == trim(strtolower($code)))
                {
                    $this->name = $cnt[$i]->name;
                    $this->code = $cnt[$i]->code;
                    $this->country = $cnt[$i]->country;
                    $this->symbol = $cnt[$i]->symbol;
                    $this->countryCode = $cnt[$i]->countryCode;

                    return;
                }
            }
        }






